import styled from 'styled-components'

export const Card = styled.div`
  padding: 8px;
  border: 1px solid #a1a1a1;
  background-color: #fcfcfc;
  color: #5e5e5e;
  border-radius:8px;
`

export const Contador = styled.div`
  font-weight: bold;
  font-size: 24px;
  display: block;
`

export const Label = styled.div`
  font-size: 14;
`
