import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
import * as S from './styles'

const LoginRegister = () => {
  const [aba, setAba] = useState<'login' | 'cadastro'>('login')
  const [form, setForm] = useState({ email: '', password: '', name: '', surname: '' })
  const navigate = useNavigate()

  const aoMudar = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value })
  }

  const aoEnviar = async (e: React.FormEvent) => {
    e.preventDefault()
    try {
      if (aba === 'cadastro') {
        await axios.post('http://localhost:4000/Users', {
          email: form.email,
          password: form.password,
          name: form.name,
          surname: form.surname,
          city: 'Lux',
          timezone: 'UTC'
        })
        alert('Usuário cadastrado!')
        setAba('login')
      } else {
        const res = await axios.post('http://localhost:4000/login', {
          email: form.email,
          password: form.password
        })
        localStorage.setItem('user', JSON.stringify((res.data as any).user))
        navigate('/tarefas')
      }
    } catch (error: any) {
      alert(error.response?.data?.error || 'Erro ao processar solicitação')
    }
  }

  return (
    <S.Container>
      <div>
        <S.Tab ativa={aba === 'login'} onClick={() => setAba('login')}>
          Login
        </S.Tab>
        <S.Tab ativa={aba === 'cadastro'} onClick={() => setAba('cadastro')}>
          Cadastro
        </S.Tab>
      </div>

      <S.Formulario onSubmit={aoEnviar}>
        {aba === 'cadastro' && (
          <>
            <S.Campo name="name" placeholder="Nome" value={form.name} onChange={aoMudar} required />
            <S.Campo name="surname" placeholder="Sobrenome" value={form.surname} onChange={aoMudar} required />
          </>
        )}
        <S.Campo type="email" name="email" placeholder="Email" value={form.email} onChange={aoMudar} required />
        <S.Campo type="password" name="password" placeholder="Senha" value={form.password} onChange={aoMudar} required />
        <S.Botao type="submit">{aba === 'login' ? 'Entrar' : 'Cadastrar'}</S.Botao>
      </S.Formulario>
    </S.Container>
  )
}

export default LoginRegister
