import styled from 'styled-components'

export const Container = styled.div`
  max-width: 400px;
  margin: 4rem auto;
  padding: 2rem;
  background-color: #1c2833;
  border-radius: 8px;
  color: #fff;
  font-family: sans-serif;
`

export const Tabs = styled.div`
  display: flex;
`

export const TabButton = styled.button<{ active: boolean }>`
  flex: 1;
  padding: 1rem;
  background: ${({ active }) => (active ? '#1abc9c' : '#2c3e50')};
  color: white;
  border: none;
  cursor: pointer;
  font-weight: bold;
`

export const Title = styled.h2`
  text-align: center;
  margin: 2rem 0 1rem;
`

export const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`

export const InputGroup = styled.div`
  display: flex;
  gap: 1rem;

  input {
    flex: 1;
  }
`

export const Input = styled.input`
  padding: 0.75rem;
  border: 1px solid #666;
  background: #2c3e50;
  color: white;
  border-radius: 4px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`

export const SubmitButton = styled.button`
  background: #1abc9c;
  padding: 1rem;
  font-size: 1.2rem;
  font-weight: bold;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background: #17a589;
  }
`
