import { createSlice } from '@reduxjs/toolkit';
import Tarefa from '../../models/Tarefas';
import * as enums from '../../containers/utils/enums/Tarefa';
const tarefasSlice = createSlice({
  name: 'tarefas',
  initialState: [new Tarefa('Estudar Java', enums.Prioridade.IMPORTANTE, enums.Status.PENDENTE, ' estudar pra prova', 1), new Tarefa('Estudar C', enums.Prioridade.NORMAL, enums.Status.PENDENTE, ' estudar no curso em video', 2), new Tarefa('Estudar Ruby on Rails', enums.Prioridade.URGENTE, enums.Status.CONCLUIDA, ' estudar pra prova', 3)],
  reducers: {
    remover: (state, action) => {
      state = state.filter(tarefa => tarefa.id !== action.payload);
    }
  }
});
export const {
  remover
} = tarefasSlice.actions;
export default tarefasSlice.reducer;
