import { useState } from 'react'
import { UseDispatch, useDispatch } from 'react-redux'

import { BotaoSalva, MainContainer, Titulo } from '../../styles'
import { Campo } from '../../styles'
import { Form, Opcoes } from './styles'

const Formulario = () => {
  const dispatch = useDispatch()
  const [titulo, setTitulo] = useState('')
  const [descricao, setDescricao] = useState('')
  return (
    <MainContainer>
      <Titulo>Nouvelle tâche</Titulo>
      <Form>
        <Campo
        value={titulo}
        onChange={(evento) => setTitulo}
        type="text" placeholder="Titre" />
        <Campo placeholder="Description de la tâche" />
        <Opcoes>
          <p>Priorité</p>
          <input name="prioridade" type="radio" id="urgente" />{' '}
          <label htmlFor="urgente">Urgent</label>
          <input name="prioridade" type="radio" id="importante" />{' '}
          <label htmlFor="importante">Important</label>
          <input name="prioridade" type="radio" id="mormal" />{' '}
          <label htmlFor="normal">Normal</label>
        </Opcoes>

        <BotaoSalva type="submit">Registre</BotaoSalva>
      </Form>
    </MainContainer>
  )
}

export default Formulario
